import { Component, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-shopping-cart',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.css']
})
export class ShoppingCartComponent {
  itensDoCarrinho = [
    { id: 1, name: 'Alicate Wiha', price: 10.99, quantity: 1 },
    { id: 2, name: 'Arroz 1Kg', price: 8.09, quantity: 2 },
    { id: 3, name: 'Feijão 1Kg', price: 7.99, quantity: 1 },
    { id: 4, name: 'Lanterna LED dewalt', price: 20.99, quantity: 1 },
    { id: 5, name: 'Sabonete novo perfume', price: 0.99, quantity: 3 },
    { id: 6, name: 'Óleo de soja', price: 4.99, quantity: 2 },
    { id: 7, name: 'Carne boa', price: 25.99, quantity: 1 },
    { id: 8, name: 'SSD Western Digital 1TB', price: 99.00, quantity: 1 },
    { id: 9, name: 'Processador intel i9', price: 170.99, quantity: 1 },
    { id: 10, name: 'Gabinete', price: 100.87, quantity: 1 }
  ];

  listaDeItens = signal(this.itensDoCarrinho);

  precoTotal = computed(() => {
    return this.listaDeItens().reduce((acc, item) => acc + (item.price * item.quantity), 0);
  });

  removeItem(index: number) {
    this.listaDeItens.set(this.listaDeItens().filter((item, i) => i !== index));
  }

}
<div class="cart">
    <h2>Seu carrinho de compras</h2>

    <div *ngFor="let item of listaDeItens(); let i = index">

      <div class="cart-item">
        <p>{{item.name}}</p>
        <p>{{item.price}}</p>
        <button (click)="removeItem(i)">Remove</button>
      </div>
    </div>
    <div class="cart-total">
      <h3>Total: {{precoTotal()}}</h3>
    </div>
  </div>

